css:
/*pc*/@media (min-width: 601px){
  .top-bar{display:none;}
.header__logo {
    max-width: 300px;
    max-height: 70px;
  }
.products-carousel__title a {
    font-family: 'Outfit', sans-serif;
    font-size: 28px;
    color: #525252;
}

}
 /*mobile*/@media (max-width: 600px) {
.header__logo {
    max-width: 100px;
    max-height: 70px;
  }

.products-carousel__title a {
    font-family: 'Outfit', sans-serif;
    font-size: 20px;
    color: #525252;
}

}
/*responsivo*/

  .header{  
    background: rgb(5,22,120);
background: linear-gradient(90deg, rgba(5,22,120,1) 0%, rgba(17,171,152,1) 35%, rgba(107,22,134,1) 100%);}

.product__short-stock{display:none;}

.top-bar__countdown {font-family: 'Outfit', sans-serif;
  font-size: 14px;}
.top-bar__page-links a {font-family: 'Outfit', sans-serif;
  font-size: 14px;}
.top-bar__page-links {font-family: 'Outfit', sans-serif;
  font-size: 14px;}

.prices__value{color: #000;font-family: 'Outfit', sans-serif;font-size: 19px;}
.btn-primary{font-family: 'Outfit', sans-serif;}
.header__nav {text-transform: none;color: #565656;font-family: 'Outfit', sans-serif;  font-size: 21px;}
.header__nav--full a {text-transform: none;color:#fff;font-family: 'Outfit', sans-serif;  font-size: 21px;}
.header__nav--full a:hover {text-transform: none;color: #fff;background:transparent; font-family: 'Outfit', sans-serif;  font-size: 21px;}
.body{font-family: 'Outfit', sans-serif;font-size: 15px;}

.menu__list {
    background: rgb(5,22,120);
