css:
/*pc*/@media (min-width: 601px){
.header__logo {
    max-width: 300px;
    max-height: 70px;
  }}
 /*mobile*/@media (max-width: 600px) {
.header__logo {
    max-width: 100px;
    max-height: 70px;
  }}
/*responsivo*/


.product__short-stock{display:none;}

.top-bar__countdown {font-family: 'Outfit', sans-serif;
  font-size: 14px;}
.top-bar__page-links a {font-family: 'Outfit', sans-serif;
  font-size: 14px;}
.top-bar__page-links {font-family: 'Outfit', sans-serif;
  font-size: 14px;}
.products-carousel__title a{font-family: 'Outfit', sans-serif;font-size: 28px;color: #b0191f;}
.prices__value{color: #b0191f;font-family: 'Outfit', sans-serif;font-size: 19px;}
.btn-primary{font-family: 'Outfit', sans-serif;}
.header__nav {text-transform: none;color: #565656;font-family: 'Outfit', sans-serif;  font-size: 21px;}
.header__nav--full a {text-transform: none;color:#000000;font-family: 'Outfit', sans-serif;  font-size: 21px;}
.header__nav--full a:hover {text-transform: none;color: #b0191f;background:transparent; font-family: 'Outfit', sans-serif;  font-size: 21px;}
.body{font-family: 'Outfit', sans-serif;font-size: 15px;}
.header{background: #fff;} 
.menu__list {
    background-color: #b93e47;
}
.menu{ background-color: #b93e47;
}
.menu__footer a {
    color: #ffffff;
    text-decoration: none;font-family: 'Outfit', sans-serif;font-size: 18px;
}
.menu__link {color: #ffffff;font-family: 'Outfit', sans-serif;}
.menu__link :hover{color: #ffffff;font-family: 'Outfit', sans-serif;}
.menu__social{display:none;}
.menu__item{font-family: 'Outfit', sans-serif;font-size: 18px;color: #fff;border-bottom: var(--border-width) solid #ffffff00;}
.menu__item a:hover{font-family: 'Outfit', sans-serif;font-size: 18px;color:#b0191f;background:transparent;border-bottom: var(--border-width) solid #ffffff;}
.page-title__short-description.lead{font-family: 'Outfit', sans-serif;font-size: 16px;}
p{font-family: 'Outfit', sans-serif;font-size: 15px;}
h1{font-family: 'Outfit', sans-serif;font-size: 28px;color: #b0191f;}
h2{font-family: 'Outfit', sans-serif;font-size: 16px;}
.product-card__name{font-family: 'Outfit', sans-serif;font-size: 16px;color: #565656;}
.header__search-input{font-size: 16px;font-family: 'Outfit', sans-serif;}
.header{border-bottom: 1px solid #e5e4e4;}
.header__buttons .btn {font-size: 28px;color:#000;}
.header__search-btn { font-size: 28px;}
.header__search-input{height: 50px;}
.footer {
    background: #eaeaea;
    border-top: var(--spacer-1) solid #f9f9f9;
}.footer__store {
  color: #f3f3f3;}
.footer__credits{font-family: 'Outfit', sans-serif;font-size: 13px;color: #6a6a6a;border-top: var(--border-width) solid #e9e9e9;}
.footer__title{font-family: 'Outfit', sans-serif;font-size: 25px;color: #b0191f;}
.col-12 .col-xl .order-lg-first{color: #f3f3f3;}
.col-12.col-lg-auto.order-lg-last.pl-xl-4{color: #f3f3f3;}
.address{font-family: 'Outfit', sans-serif;font-size: 15px;color: #f3f3f3;}
.footer__links{font-family: 'Outfit', sans-serif;font-size: 18px;color: #6a6a6a;}
.footer__links a {font-family: 'Outfit', sans-serif;font-size: 18px;color: #6a6a6a;}
.footer__links a:hover{font-family: 'Outfit', sans-serif;font-size: 18px;color: #6a6a6a;}
.list-unstyled{font-family: 'Outfit', sans-serif;font-size: 18px;color: #6a6a6a;}
.label{font-family: 'Outfit', sans-serif;font-size: 15px;color: #6a6a6a;}
.html-clearfix{font-family: 'Outfit', sans-serif;font-size: 15px;}
.ecom-credits {
    padding: 1.5rem;
  background: #eaeaea;}
.product__sku {   font-size: 12px;}
.prices--big .prices__value {
  font-size: 28px;}
h5{font-family: 'Outfit', sans-serif;font-size: 18px;color: #6a6a6a;}
.breadcrumb-item.active{font-family: 'Outfit', sans-serif;font-size: 15px;color: #6a6a6a;}
.share-network-whatsapp{font-family: 'Outfit', sans-serif;font-size: 13px;color: #6a6a6a;}
.share-network-facebook{display:none;}
.share-network-twitter{display:none;}
